import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

class App extends React.Component {

    state = {
        inputValue: "bbb",
        filter: "all",
        prefectures: []
    };


    componentDidMount() {
        fetch('https://opendata.resas-portal.go.jp/api/v1/prefectures', {
            headers: { 'X-API-KEY': 'laQNIVQSq86KoGBF9t0pEsDTR5h9Ejs9EXm1spwN' }
        })
        .then(response => response.json())
        .then(res => {
            // console.log(res.result[1].prefName); ⇨ 青森県
            // console.log(res.result); ⇨ {prefCode: 1 prefName: "北海道"},{prefCode: 2, prefName: "青森県"},,,
            this.setState({ prefectures: res.result });
        });
    }



  render() {

    return (
        <div>
        <form onSubmit={this.addTodo}>
          <input
            type="text"
            name="inputTodo"
            value={this.state.inputValue}
            onChange={this.changeInput}
          />
          <input type="submit" value="ToDo追加" />
        </form>
        {this.state.error ? (
          <div className="errorMessage">{this.state.error}</div>
        ) : null}
        <button onClick={this.allDisplay}>All</button>
        <button onClick={this.activeDisplay}>Active</button>
        <button onClick={this.completedDisplay}>Completed</button>
        <button onClick={this.clearCompleted}>ClearCompleted</button>
        {displayTodos.map((todo, index) => {
          return (
            <div key={todo.id}>
              <span
                className={todo.isDone ? "todoContent" : ""}
                onClick={() => this.doneTodo(index)}
              >
                {todo.isDone ? "[X]" : "[]"} id: {todo.id} {todo.todo}
              </span>
              <span
                className="deleteButton"
                onClick={() => this.deleteTodo(index)}
              >
                X
              </span>
            </div>
          );
        })}
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
